INSERT INTO `tbl_category_blog` (`id`, `category_name`, `category_slug`, `category_description`, `category_status`, `created_at`, `updated_at`) VALUES
(1, 'Đánh giá & so sánh sản phẩm', 'danh-gia-&-so-sanh-san-pham', 'Blog tập trung vào việc đánh giá và so sánh các sản phẩm giày từ các thương hiệu khác nhau. Các bài viết trong danh mục này thường cung cấp đánh giá chất lượng, tính năng và sự thoải mái của từng loại giày, giúp độc giả dễ dàng chọn lựa sản phẩm phù hợp với nhu cầu của họ.', 1, NULL, NULL),
(2, 'Xu hướng & Phong cách', 'xu-huong-&-phong-cach', 'Blog này tập trung vào việc thảo luận về các xu hướng mới trong thế giới giày dép và cách kết hợp chúng với trang phục để tạo nên phong cách thời trang độc đáo và cá nhân.', 1, NULL, NULL),
(3, 'Tin tức & Sự kiện', 'tin-tuc-&-su-kien', 'Danh mục này cung cấp các thông tin mới nhất về các sự kiện, triển lãm, và tin tức quan trọng trong ngành công nghiệp giày dép, bao gồm cả việc ra mắt sản phẩm mới, cải tiến công nghệ, và các hợp tác giữa các thương hiệu lớn', 1, NULL, NULL),
(4, 'Chăm sóc & Bảo dưỡng', 'cham-soc-&-bao-duong', 'Blog này cung cấp các hướng dẫn và mẹo về cách chăm sóc và bảo dưỡng giày, bao gồm cả cách làm sạch, bảo quản, và sửa chữa các vấn đề như hỏng hóc hay mòn trầy.', 1, NULL, NULL),
(5, 'Lịch sử & Công nghệ sản xuất', 'lich-su-&-cong-nghe-san-xuat', 'Danh mục này cung cấp kiến thức về lịch sử của giày dép, bao gồm cả quá trình phát triển của ngành công nghiệp giày dép và công nghệ sản xuất giày từ ngày xưa đến nay.', 1, NULL, NULL),
